create table ValuableItem (pk INT IDENTITY PRIMARY KEY, id VARCHAR(250) NOT NULL, subscriptionFk INT NOT NULL, item JSON NOT NULL, estimatedPrice INT NOT NULL, createdDate TIMESTAMP NOT NULL)
create table Subscription (pk INT IDENTITY PRIMARY KEY, tabIds ARRAY NOT NULL, threshold VARCHAR(250) NOT NULL, thresholdCurrencyType VARCHAR(250) NOT NULL)
create table User (pk INT IDENTITY PRIMARY KEY, league VARCHAR(250) NOT NULL, accountName VARCHAR(250) NOT NULL, realm VARCHAR(250) NOT NULL, sessionId VARCHAR(250) NOT NULL)